        box-shadow: 0 1px 2px rgba(0, 0, 0, 0.5) inset;
        overflow: hidden;
        border-radius: 2px;
        position: relative;
        cursor: pointer;
    }

    .music-player .player_progress .back {
        width: 0px;
        height: 100%;
        border-radius: 2px;
        background: rgb(12, 182, 212);
    }

    .music-player .player_progress .pointer {
        width: 7px;
        height: 7px;
        background: #fff;
        border-radius: 50%;
        opacity: 0;
        -webkit-transition: opacity 0.3s;
        -o-transition: opacity 0.3s;
        transition: opacity 0.3s;
        position: absolute;
        top: -1px;
        left: 0;
    }

    .music-player .player_progress:hover .pointer {
        opacity: 1;
    }


    /* 播放 画片 动画 */

    @-webkit-keyframes disc {
        from {
            -webkit-transform: rotate(0deg);
            transform: rotate(0deg);
        }

        to {
            -webkit-transform: rotate(360deg);
            transform: rotate(360deg);
        }
    }

    @keyframes disc {
        from {
            -webkit-transform: rotate(0deg);
            transform: rotate(0deg);
        }

        to {
            -webkit-transform: rotate(360deg);
            transform: rotate(360deg);
        }
    }

    .music-player .music-player__image.play {
        -webkit-animation: disc 5s linear 0s infinite;
        animation: disc 5s linear 0s infinite;
    }

    /*  播放进度  */
    .music-player .player__song--progress {
        width: 100%;
        margin-top: 15px;
    }
}
